-- View the table
SELECT * FROM  .customers;

-- Q1. Total revenue generated by male vs. female customers
SELECT gender, 
       SUM(purchase_amount_usd) AS revenue
FROM customers
GROUP BY gender;

-- Q2. Customers who used a discount but spent more than the average purchase amount
SELECT customer_id, purchase_amount_usd
FROM customers
WHERE discount_applied = 'Yes' 
  AND purchase_amount_usd >= (SELECT AVG(purchase_amount_usd) FROM customers);

-- Q3. Top 5 products with highest average review rating
SELECT item_purchased, 
       ROUND(AVG(review_rating), 2) AS avg_product_rating
FROM customers
GROUP BY item_purchased
ORDER BY avg_product_rating DESC
LIMIT 5;

-- Q4. Compare the average Purchase Amounts between Standard and Express Shipping
SELECT shipping_type, 
       ROUND(AVG(purchase_amount_usd), 2) AS avg_purchase_amount
FROM customers
WHERE shipping_type IN ('Standard','Express')
GROUP BY shipping_type;

-- Q5. Do subscribed customers spend more? Compare average spend and total revenue
SELECT subscription_status,
       COUNT(customer_id) AS total_customers,
       ROUND(AVG(purchase_amount_usd), 2) AS avg_spend,
       ROUND(SUM(purchase_amount_usd), 2) AS total_revenue
FROM customers
GROUP BY subscription_status
ORDER BY total_revenue DESC, avg_spend DESC;

-- Q6. Top 5 products with the highest percentage of purchases with discounts applied
SELECT item_purchased,
       ROUND(100.0 * SUM(CASE WHEN discount_applied = 'Yes' THEN 1 ELSE 0 END)/COUNT(*), 2) AS discount_rate
FROM customers
GROUP BY item_purchased
ORDER BY discount_rate DESC
LIMIT 5;

-- Q7. Segment customers into New, Returning, and Loyal based on previous purchases
WITH customer_type AS (
    SELECT customer_id, previous_purchases,
           CASE 
               WHEN previous_purchases = 1 THEN 'New'
               WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
               ELSE 'Loyal'
           END AS customer_segment
    FROM customers
)
SELECT customer_segment, COUNT(*) AS num_customers
FROM customer_type
GROUP BY customer_segment;

-- Q8. Top 3 most purchased products within each category
WITH item_counts AS (
    SELECT category,
           item_purchased,
           COUNT(customer_id) AS total_orders,
           ROW_NUMBER() OVER (PARTITION BY category ORDER BY COUNT(customer_id) DESC) AS item_rank
    FROM customers
    GROUP BY category, item_purchased
)
SELECT item_rank, category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <= 3;

-- Q9. Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
SELECT subscription_status,
       COUNT(customer_id) AS repeat_buyers
FROM customers
WHERE previous_purchases > 5
GROUP BY subscription_status;

-- Q10. Revenue contribution of each age group
SELECT age_group,
       ROUND(SUM(purchase_amount_usd), 2) AS total_revenue
FROM customers
GROUP BY age_group
ORDER BY total_revenue DESC;
